(176, 'theme_mods_jobscout', 'a:9:{i:0;b:0;s:18:\"nav_menu_locations\";a:2:{s:7:\"primary\";i:30;s:9:\"secondary\";i:0;}s:18:\"custom_css_post_id\";i:-1;s:12:\"banner_title\";s:61:\"Bứt Phá Giới Hạn – Kiến Tạo Giấc Mơ Tỷ Phú\";s:15:\"banner_subtitle\";s:202:\"Mỗi công việc là một bước tiến. Cùng chúng tôi bứt phá giới hạn, phát triển kỹ năng và biến ước mơ về một tương lai thành công, sung túc thành hiện thực\";s:12:\"header_image\";s:98:\"http://localhost/CMS-CuoiKy.git/wp-content/uploads/2025/11/cropped-to-reach-looking-career-job.jpg\";s:17:\"header_image_data\";O:8:\"stdClass\":5:{s:13:\"attachment_id\";i:1001;s:3:\"url\";s:98:\"http://localhost/CMS-CuoiKy.git/wp-content/uploads/2025/11/cropped-to-reach-looking-career-job.jpg\";s:13:\"thumbnail_url\";s:98:\"http://localhost/CMS-CuoiKy.git/wp-content/uploads/2025/11/cropped-to-reach-looking-career-job.jpg\";s:6:\"height\";i:704;s:5:\"width\";i:1920;}s:11:\"custom_logo\";i:1005;s:14:\"post_job_label\";s:10:\"SUBMIT JOB\";}', 'on'),
