(211, 'widget_rtc_social_links', 'a:2:{i:1;a:5:{s:5:\"title\";s:20:\"Subscribe and Follow\";s:6:\"target\";N;s:4:\"size\";N;s:6:\"social\";a:6:{i:1;s:32:\"https://facebook.com/rarathemehq\";i:2;s:31:\"https://twitter.com/rarathemehq\";i:3;s:20:\"https://linkedin.com\";i:4;s:21:\"https://pinterest.com\";i:5;s:25:\"https://google-plus-g.com\";i:6;s:21:\"https://instagram.com\";}s:14:\"social_profile\";a:6:{i:1;s:8:\"facebook\";i:2;s:7:\"twitter\";i:3;s:8:\"linkedin\";i:4;s:9:\"pinterest\";i:5;s:13:\"google-plus-g\";i:6;s:9:\"instagram\";}}s:12:\"_multiwidget\";i:1;}', 'auto'),
(212, 'widget_rrtc_testimonial_widget', 'a:5:{i:1;a:4:{s:4:\"name\";s:15:\"Teressa Jackson\";s:11:\"testimonial\";s:210:\"We never thought of finding to stand alone, we\'ll never let you fall. Don\'t need permission to decide what you believe. You gotta learn something when we meet you after school. I said jump, down on Jump Street.\";s:5:\"image\";s:2:\"48\";s:11:\"designation\";s:13:\"Art Direction\";}i:2;a:4:{s:4:\"name\";s:10:\"Ben Parker\";s:11:\"testimonial\";s:249:\"Dance your cares away, worry\'s for another day. Let the music play, down at Fraggle Rock! Work you cares away, dancing\'s for another day. Let the Fraggles play, We\'re Gobo, Mokey, Wembley, Boober, Red! Dance your cares away, worry\'s for another day.\";s:5:\"image\";s:2:\"50\";s:11:\"designation\";s:20:\"Marketing Superviser\";}i:3;a:4:{s:4:\"name\";s:15:\"Amelie Anderson\";s:11:\"testimonial\";s:249:\"An endorsement is typically a well-known influencer giving their public support for a brand. But a testimonial is from a customer or client. They may be an unknown person to the reader, but they have personal experience with the product or service. \";s:5:\"image\";s:2:\"52\";s:11:\"designation\";s:18:\"CEO of XYZ Company\";}i:4;a:4:{s:4:\"name\";s:14:\"Thomas Walters\";s:11:\"testimonial\";s:265:\"Such a review may evaluate the book on the basis of personal taste. Reviewers may use the occasion of a book review for an extended essay that can be closely or loosely related to the subject of the book, or to promulgate their own ideas on the topic of a fiction .\";s:5:\"image\";s:2:\"53\";s:11:\"designation\";s:16:\"Developer at ABC\";}s:12:\"_multiwidget\";i:1;}', 'auto'),
